import { applyProps, addEffect } from '@react-three/fiber'

import { Globals } from '@react-spring/core'
import { createStringInterpolator, colors, raf } from '@react-spring/shared'
import { createHost } from '@react-spring/animated'

import { primitives } from './primitives'
import { WithAnimated } from './animated'

Globals.assign({
  createStringInterpolator,
  colors,
  frameLoop: 'demand',
})

// Let r3f drive the frameloop.
addEffect(() => {
  raf.advance()
})

const host = createHost(primitives, {
  // @ts-expect-error r3f related
  applyAnimatedValues: applyProps,
})

export const animated = host.animated as WithAnimated
export { animated as a }

export * from './animated'
export * from '@react-spring/core'
import * as THREE from 'three'
import '@react-three/fiber'

export type Primitives = keyof JSX.IntrinsicElements

export const primitives = ['primitive'].concat(
  Object.keys(THREE)
    .filter(key => /^[A-Z]/.test(key))
    .map(key => key[0].toLowerCase() + key.slice(1))
) as Primitives[]
